DROP TABLE IF EXISTS "credenziali";
CREATE TABLE "credenziali" ("id_utente_cred" INTEGER PRIMARY KEY  NOT NULL , "password" VARCHAR NOT NULL );
DROP TABLE IF EXISTS "problemi";
CREATE TABLE "problemi" ("id_problema" INTEGER PRIMARY KEY  NOT NULL , "tipo_problema" VARCHAR NOT NULL, "soluzione" VARCHAR, "tipo_barca" VARCHAR, "tipologia" VARCHAR);
DROP TABLE IF EXISTS "utenti";
CREATE TABLE "utenti" ("id_utente" INTEGER PRIMARY KEY  NOT NULL , "nome" VARCHAR NOT NULL , "cognome" VARCHAR NOT NULL , "numerotel" VARCHAR NOT NULL , "indirizzo" VARCHAR NOT NULL );
